<?phpclass product{    public static function get_slider_images_one($product_id)    {        if(!$product_id){            return 0;        }        $slider_images = get_post_meta($product_id , 'slider_images',true);        if(!empty($slider_images) && is_array($slider_images) && count($slider_images)>0 ){            return $slider_images[0];        }        return false;    }    public static function get_slider_images($product_id)    {        if(!$product_id){            return 0;        }        $slider_images = get_post_meta($product_id , 'slider_images',true);        if(!empty($slider_images) && is_array($slider_images) && count($slider_images)>0 ){            return $slider_images;        }        return false;    }    public static function get_price_product($product_id)    {        $price = get_post_meta($product_id,'product_price',true);        if(intval($price)){            return $price ;        }        return false;    }    public static function get_price_product_final($product_id)    {        $price_final = get_post_meta($product_id,'product_price_delete',true);        if(intval($price_final)){            return $price_final ;        }        return false;    }    public static function get_product_to_basket($product_id)    {        $product = get_post($product_id);        return $product;    }}