<?phpclass slidermetabox{    public static function register_product_images_meta_box()    {        add_meta_box(            'product_image_metabox',            'عکس های محصول',            'slidermetabox::product_images_content_handler',            'product'        );    }    public static function product_images_content_handler($post)    {        $slider_images = get_post_meta($post->ID,'slider_images',true);        view::renderFile('admin.metabox.product.product_images',array('slider_images' => $slider_images));    }    public static function save_product_images($postid)    {        if(isset($_POST['slider'] ) && count($_POST['slider']) > 0 ) {            update_post_meta($postid, 'slider_images', $_POST['slider']);        }    }}