<?phpclass view{    public static function __callstatic($name, $arguements)    {        switch ($name):            case 'render':                self::render_view($arguements[0], $arguements[1]);                break;            case 'renderFile':                self::render_view_file_or_varible($arguements[0], $arguements[1]);                break;        endswitch;    }    private static function render_view($view_name)    {       get_template_part('views/' . $view_name);    }    public static function render_view_file_or_varible($view_name, $data)    {        $view_name = str_replace('.',DIRECTORY_SEPARATOR,$view_name);        $view_file_path = THEME_VIEW.DIRECTORY_SEPARATOR.$view_name. '.php';        if(is_file($view_file_path) && is_readable($view_file_path)){            !empty($data)? extract($data):null;             include $view_file_path;        }    }}