<?php /* TEMPLATE NAME:subscribe */ ?><?php    if(isset($_POST['subscribe_form_submit']) ){        if(empty($_POST['mobile'])){            $has_error = true;            $message_error[] = 'پر کردن فیلد الزامی است';        }        $ruls = [                'mobile' => 'required|string|regex:/09[]{11}/'        ];        if(!$has_error) {            global $wpdb, $table_prefix;            $insert_data = [                'mobile' => sanitize_text_field($_POST['mobile']),                'create_at' => date('Y-m-d H:i:s'),            ];            $table_name = $table_prefix . 'subscribe';            $wpdb->insert($table_name, $insert_data, [                '%s', '%s'            ]);            $wpdb->insert_id;        }    }?><div class="wrapper">    <?php if($has_error): ?>    <p><?php echo $message_error ?></p>    <?php endif; ?>    <form action="" method="post">        <div class="form-row">            <input type="text" name="mobile" placeholder="شماره همراه">        </div>        <div class="form-row">            <input type="submit" name="subscribe_form_submit" value = "ارسال">        </div>    </form></div>